from collections import Counter


class Solution:
    def validPalindrome(self, s: str) -> bool:

        d = Counter(s)

        if s == "".join(reversed(list(s))):
            return True
        else:

            for i in range(len(s)):
                s2 = s[:i] + s[i + 1:]
                if s2 == "".join(reversed(list(s2))):
                    return True

        return False


if __name__ == '__main__':
    s = ""
    solution = Solution()

    print(solution.validPalindrome(s))

